import React, { useState } from 'react';
import Nav from '../components/Nav';
import TopBar from '../components/TopBar';
import data from '../data.json'

const Crew = () => {
    const { crew } = data

    const [currentCrew, setCurrentCrew] = useState(crew[0])

    return (
        <section id="crew" className='background-img-full'>
            <Nav />
            <div className="container">
                <div className="row">
                    <TopBar index={'02'} para='MEET YOUR CREW' />                 
                    <div className="crew__slideshow--frame">
                        <div className="crew__slideshow--description">
                            <h3 className="crew__slideshow--sub-title">COMMANDER</h3>
                            <h1 className="crew__slideshow--title">DOUGLAS HURLEY</h1>
                            <p className="crew__slideshow--title">Lorem ipsum dolor sit, amet consectetur adipisicing elit. Laboriosam magnam, enim atque, saepe corporis beatae odit voluptas eum quas molestias qui deleniti sequi veniam! Odit est explicabo sed ad ullam.</p>
                        </div>
                        <figure className="crew__img--wrapper">
                            <img src="https://space-tourism-multi-page-solution.netlify.app/images/e4c7def427824ef543c0.webp" alt="" className="crew__img" />
                        </figure>
                    </div>                    
                </div>
            </div>
        </section>
    );
}

export default Crew;
