import React from 'react';
import Logo from '../assets/shared/logo.svg'
import { Link, useLocation} from 'react-router-dom'

const Nav = () => {
    const { pathname } = useLocation()
    console.log(pathname === '/')
    const links = [
        {
            text: 'HOME',
            destination: '/'
        },
        {
            text: 'DESTINATIONS',
            destination: '/destinations'
        },
        {
            text: 'CREW',
            destination: '/crew'
        },
        {
            text: 'TECHNOLOGY',
            destination: '/technology'
        }
    ]
    return (
        <nav>
            <div className="nav__container">
                <img src={Logo} alt="" className="nav__logo" />
                <div className="nav__line"></div>
                <ul className="nav__links">
                    <li className="nav__link">
                        <Link to='/' className={`nav__link--anchor ${pathname === '/' ? 'nav__link--current' : ''}`}><strong>00</strong> HOME</Link>
                    </li>
                    <li className="nav__link">
                        <Link to='/destinations' className={`nav__link--anchor ${pathname === '/destinations' ? 'nav__link--current' : ''}`}><strong>01</strong> DESTINATION</Link>
                    </li>
                    <li className="nav__link">
                        <Link to='/crew' className='nav__link--anchor'><strong>02</strong> CREW</Link>
                    </li>
                    <li className="nav__link">
                        <Link to='/technology' className='nav__link--anchor'><strong>03</strong> TECHNOLOGY</Link>
                    </li>
                    {
                        links.map((item, index) => (
                            <li className="nav__link">
                                <Link to={item.destination} className={`nav__link--anchor ${pathname === item.destination ? 'nav__link--current' : ''}`}><strong>0{index + 1}</strong>{item.text}</Link>
                            </li>
                        ))
                    }
                </ul>
            </div>
        </nav>
    );
}

export default Nav;
